    function submitaddTemplate(event){
        event.preventDefault();
        let url = "{{ url('v2/dashboard/create') }}";
        var formData = new FormData(document.getElementById("dashboardForm"));
        $.ajax({
            type: 'POST',
            url: url,
            data:formData,
            processData: false,
            contentType: false,
            beforeSend: function () {
                toggleLoader(true);
            },
            success: function(data) {
                if(data.status){
                    var link="{{ url('dashboard/') }}"+ "/" +data.type;
                    $('#dashboard-link-report').attr('href',link);
                    reportSuccess.show();
                    toggleLoader(false);
                }else{
                    toastr.warning(data.message);
                    toggleLoader(false);
                }
            },
            error: function(error) {
                toastr.warning("{{__('error')}}");
                toggleLoader(false);
            }
        });
    }
