<script>
  const path = document.location.pathname;

  console.log(path)

  switch (path) {
    case '/dashboard':
    case '/dashboard/facebook-engagement':
    case '/dashboard/facebook-overview':
    case '/dashboard/facebook-ads':
    case '/dashboard/instagram-overview':
    case '/dashboard/twitter-overview':
    case '/dashboard/google-ads-overview':
    case '/dashboard/google-analytics-overview':
      document.querySelector('#main').classList.add('active');
      console.log('1')
      // document.querySelector('.connection-icon').classList.add('active');
      break;
    case '/dashboard/connections':
      document.querySelector('#connections').classList.add('active');
      console.log('2')
      // document.querySelector('.connection-icon').classList.add('active');
      break;

    case '/dashboard/templates':
      document.querySelector('#template').classList.add('active');
      console.log('3')
      // document.querySelector('.menu-icon').classList.add('active');
      break;

    case '/dashboard/user-profile':
    case '/dashboard/user-team':
    case '/dashboard/user-billing':
      document.querySelector('#settings').classList.add('active');
      console.log('4')
      // document.querySelector('.setting-icon').classList.add('active');
      break;

  }
</script>