<script>
     // Elements
    const profilePopUp = document.querySelector('.user-profile-pop-up');
    const notificationPopUp = document.querySelector('.user-notifications-pop-up');
    const profileButton = document.querySelector('.user-profile-dropdown');
    const notificationsButton = document.querySelector('.notification-icon');
    const body = document.querySelector('body');
    // Event listeners
    profileButton.addEventListener('click', profileButtonHandler);
    notificationsButton.addEventListener('click', notificationButtonHandler);
    // Click handlers
    function profileButtonHandler() {
        if (profilePopUp.classList.contains('d-none')) {
            profilePopUp.classList.remove('d-none');

            setTimeout(() => {
                body.addEventListener('click', hideProfilePopUp);
            }, 0);

            return;
        }
    }
    function notificationButtonHandler() {
        
        if (notificationPopUp.classList.contains('d-none')) {
            notificationsButton.classList.remove('active');
            notificationPopUp.classList.remove('d-none');
            setTimeout(() => {
                body.addEventListener('click', hideNotificationPopUp);
            }, 0);

            return;
        }
    }

    function hideNotificationPopUp(event) {
        body.removeEventListener('click', hideNotificationPopUp);
        $("#user-notifications-pop-up").toggleClass('d-none');
        
    }

    function hideProfilePopUp(event) {
        body.removeEventListener('click', hideProfilePopUp);
        $("#user-profile-pop-up").toggleClass('d-none');
    }
    function removeActivity(id) {
      $.ajax({
          url: '{{url('')}}' + '/view-activity/' + id,
          method: "get",
          dataType: 'json',
          success: (response) => {
          },
      });
  }
  function viewAll() {
      $.ajax({
          url: '{{url('')}}' + '/view-activity-all',
          method: "get",
          dataType: 'json',
          success: (response) => {
              $('.user-notifications-pop-up').addClass('hidden');
              $('.user-notifications-list').html('');
          },
      });
  }
</script>