    <script type="text/javascript">
        $(function() {
            // var start = moment().subtract(29, 'days');
            // var end = moment();
            let date_from = @if(!empty($date_from)) "{{$date_from}}" @else null @endif;
            let date_to = @if(!empty($date_to)) '{{$date_to}}' @else null @endif;
            if (date_from && date_to) {
                date_from=moment(date_from,'YYYY/MM/DD');
                date_to=moment(date_to,'YYYY/MM/DD');
            }else{
                date_from = moment().subtract(29, 'days');
                date_to = moment();
            }
            function cb(date_from, date_to) {
                $('#selected-range').html(date_from.format('D/M/YYYY') + ' - ' + date_to.format('D/M/YYYY'));
                var start = moment(date_from);
                var end   = moment(date_to);
                var diff = end.diff(start, 'days');
                $('#selected-days-count').html('{{__('Last')}} '+diff+' {{__('days')}}');
            }
            cb(date_from, date_to);
            $('#reportrange').daterangepicker({
                startDate: date_from,
                endDate: date_to,
                alwaysShowCalendars:true,
                autoApply:true,
                ranges: {
                '{{__('Last 7 Days')}}': [moment().subtract(7, 'days'), moment()],
                '{{__('Last 30 Days')}}': [moment().subtract(29, 'days'), moment()],
                '{{__('Last 90 Days')}}': [moment().subtract(90, 'days'), moment()],
                '{{__('This Month')}}': [moment().startOf('month'), moment().endOf('month')],
                '{{__('Last Month')}}': [moment().subtract(1, 'month').startOf('month'), moment().subtract(1, 'month').endOf('month')]
                }
            }, cb);
            $('#reportrange').on('apply.daterangepicker', function(ev, picker) {
                let f=picker.startDate.format('YYYY/MM/DD');
                let t=picker.endDate.format('YYYY/MM/DD');
                switch ("{{$template_name}}") {
                    case 'twitter-overview':
                        window.location.assign('/dashboard/twitter-overview?date_from=' + f + '&date_to=' + t)
                        break;
                    case 'facebook-overview':
                        window.location.assign('/dashboard/facebook-overview?date_from=' + f + '&date_to=' + t)
                        break;
                    case 'facebook-engagement':
                        window.location.assign('/dashboard/facebook-engagement?date_from=' + f + '&date_to=' + t)
                        break;
                    case 'facebook-ads-overview':
                        window.location.assign('/dashboard/facebook-ads-overview?date_from=' + f + '&date_to=' + t)
                        break;
                    case 'instagram-overview':
                        window.location.assign('/dashboard/instagram-overview?date_from=' + f + '&date_to=' + t)
                        break;
                    case 'google-analytics-overview':
                        window.location.assign('/dashboard/google-analytics-overview?date_from=' + f + '&date_to=' + t)
                        break;
                    case 'google-ads-overview':
                        window.location.assign('/dashboard/google-ads-overview?date_from=' + f + '&date_to=' + t)
                        break;
                    default:
                        window.location.assign('/dashboard/facebook-overview?date_from=' + f + '&date_to=' + t)
