        if (dir == 'right') {
            $('#mySlides').animate({
                scrollLeft: "+=100px"
            });
        } else {
            $('#mySlides').animate({
                scrollLeft: "-=100px"
            });
        }
        findEnd();
        click++;
    }

    function findEnd() {
        var width = $('#mySlides').outerWidth()
        var scrollWidth = $('#mySlides')[0].scrollWidth;
        var scrollLeft = $('#mySlides').scrollLeft();
        if (scrollWidth - width === Math.ceil(scrollLeft) || scrollWidth - width === Math.floor(scrollLeft) || Math.ceil(scrollWidth - width) === Math.ceil(scrollLeft) || Math.floor(scrollWidth - width) === Math.floor(scrollLeft) || Math.floor(scrollWidth - width) === Math.ceil(scrollLeft) || Math.floor(scrollWidth - width) === Math.ceil(scrollLeft)) {
            dir = 'left';
            $('#scroll-move-icon > svg').css('transform', 'scaleX(-1)');
        }
        if (scrollLeft === 0) {
            dir = 'right';
            if (click > 0) {
                $('#scroll-move-icon > svg').css('transform', 'scaleX(1)');
            }

        }
    }
</script>