<script>
    var total_followers_gender_age = echarts.init(document.getElementById('total_followers_gender_age'));
    option = {
        responsive: true,
        maintainAspectRatio: false,
        
        grid: {
            left: '5%',
            right: '5%',
            top: '10%',
            bottom: '10%'
        },
        xAxis: {
            type: 'category',
            data: followersData.dates
        },
        yAxis: {
            type: 'value'
        },
        series: [
            {
                data: followersData.followers,
                type: 'line',
                smooth: true
            }
        ]
    };
    // Display the chart using the configuration items and data just specified.
    total_followers_gender_age.setOption(option);
    window.addEventListener('resize', function() {
        total_followers_gender_age.resize();
    });
    $(document).ready(function() {
        if (window.innerWidth <= 992){
            total_followers_gender_age.resize();
        }
    });
</script>
<script>
    var page_orientation = echarts.init(document.getElementById('page_orientation'));
    option = {
        responsive: true,
        maintainAspectRatio: false,
        tooltip: {
            trigger: 'axis'
        },
        legend: {
            right: 10,
            top:0,
            data: [
                {
                    name: "{{__('Engagement')}}",
                    icon: 'circle'
                },
                {
                    name: "{{__('Reach')}}",
                    icon: 'circle'
                }
            ]
        },
        grid: {
            left: '7%',
            right: '5%',
            top: '10%',
            bottom: '10%'
        },
        xAxis: {
            type: 'category',
            boundaryGap: false,
            data: followersData.dates.map(date => {
                    if (date.length === 10) {
                        return date.slice(5)
                    }
                    ;
                    return date;
                }),
        },
        yAxis: {
            type: 'value'
        },
        color:['#E15466','#28A6D1'],
        series: [
            {
                name: "{{__('Engagement')}}",
                type: 'line',
                data: profileTrendData.engagement
            },
            {
                name: "{{__('Reach')}}",
                type: 'line',
                data: profileTrendData.reach,
            }
        ]
    };
    // Display the chart using the configuration items and data just specified.
