        $("#otp-alert-div").text("");
        event.preventDefault();
        const first=$('#o1').val();
        const second=$('#o2').val();
        const third=$('#o3').val();
        const four=$('#o4').val();
        const five=$('#o5').val();
        const six=$('#o6').val();

        const otp=first+second+third+four+five+six;
        $.ajax({
            type: 'POST',
            url: '{{ url('/signup-step-3-new') }}',
            data: {
                _token: '{{ csrf_token() }}',
                otp: otp
            },
            beforeSend: function () {
                toggleLoader(true);
            },
            success: function(data) {
                if(data.status==false){
                    toggleLoader(false);
                    toastr.warning("{{__('Wrong code, Please try Again')}}");
                }else{
                    window.location.replace("{{ url('/demo-dashboard') }}");
                }
                
            },
            error: function(error) {
                console.log(error);
                toggleLoader(false);
                toastr.warning("{{__('Too many attempts please try again later')}}");
            }
        })
    }
