    let alerted = false;
   
    const nameReg = /^([a-zA-Z]+\s)+[a-zA-Z]+$/;
    const arNameRegEx = /[\u0600-\u065F\u066A-\u06EF\u06FA-\u06FF]/;
    const allFieldsAreValid = false;
    const emailRegExNew = /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/;
    const jobRegExNew = /^[\u0600-\u065F\u066A-\u06EF\u06FA-\u06FFa-zA-Z0-9+\s]+[\u0600-\u065F\u066A-\u06EF\u06FA-\u06FFa-zA-Z0-9-_]*$/;
   
    function onFieldInput(inputName, inputElement) {
        inputElement.classList.remove('input-error-border')
        $(inputElement).parent().siblings(".error-text").text("");
        $(inputElement).parent().siblings(".error-text").html('&nbsp;');
        // $(inputElement).parent().siblings(".error-text").addClass('d-none');

        if (checkAllFieldsValid(inputName, inputElement,false)) {
            $('.add-users-btn')[0].classList.remove('disable-btn')
            $('.add-users-btn')[0].removeAttribute('disabled')
        } else {
            $('.add-users-btn')[0].classList.add('disable-btn')
            $('.add-users-btn')[0].setAttribute('disabled', 'true')
        }
    }

    function onFieldBlur(inputName, inputElement) {
        if (inputElement.value === '') {
            $(inputElement).parent().siblings(".error-text").text("{{__('This field is required')}}");
            // $(inputElement).parent().siblings(".error-text").removeClass('d-none');
            $('.error-text').removeClass('d-none');
            inputElement.classList.add('input-error-border')
        }

        switch (inputName) {
            case 'email':
                if (!emailRegExNew.test(inputElement.value)) {
                    $(inputElement).parent().siblings(".error-text").text("{{__('Email is not Valid')}}");
                    $(inputElement).parent().siblings(".error-text").removeClass('d-none');
                    inputElement.classList.add('input-error-border')
                }
                break;
        }
    }
    function checkAllFieldsValid(fieldName, fieldValue,final_check) {
        let allFieldsValid = true
        if(!final_check){
            fieldValue.classList.remove('input-error-border')
            $(fieldValue).parent().siblings(".error-text").text("");
            $(fieldValue).parent().siblings(".error-text").html('&nbsp;');
        // $(fieldValue).parent().siblings(".error-text").addClass('d-none');
        }
        let allEmailsInput = document.querySelectorAll('.invite-input.email');
        let allNamesInput = document.querySelectorAll('.invite-input.name');
        let allJobPositionInput = document.querySelectorAll('.invite-inputt.job-title');
        let allLocationInput = document.querySelectorAll('.invite-input.location');
        let allRoleInput = document.querySelectorAll('.invite-input.role');

        allEmailsInput.forEach(emailField => {
            if (emailField.value === '') {
                allFieldsValid = false
            }
            if (emailField.value !== '' && !emailRegExNew.test(emailField.value)) {
                allFieldsValid = false
            }
        })

        if (!allFieldsValid) {
            return false
        }

        allNamesInput.forEach(nameField => {
            if (nameField.value === '') {
                allFieldsValid = false
            }
        })

        if (!allFieldsValid) {
            return false
        }
        allJobPositionInput.forEach(jobPositionField => {
            if (jobPositionField.value === '') {
                allFieldsValid = false
            }
        })
        if (!allFieldsValid) {
            return false
        }
        allLocationInput.forEach(locationField => {
            if (locationField.value === '') {
                allFieldsValid = false
            }
        })
        if (!allFieldsValid) {
            return false
        }
        allRoleInput.forEach(roleField => {
            if (roleField.value === '') {
                allFieldsValid = false
            }
        })
        if (!allFieldsValid) {
            return false
        }

        return allFieldsValid;
    }
