    function checkAbilityAddUser() {
        let result = false;
        $.ajax({
            url: '{{url('/check-add-members')}}',
            method: "get",
            async: false,
            success: function (data) {
                result = data.status;
            }
        })
        return result;
    };

    async function addUsers(event) {
        event.preventDefault();
        if (checkAllFieldsValid('none','none',true)) {
            $('.add-users-btn')[0].classList.remove('disable-btn')
            $('.add-users-btn')[0].removeAttribute('disabled')
        } else {
            $('.add-users-btn')[0].classList.add('disable-btn')
            $('.add-users-btn')[0].setAttribute('disabled', 'true')
            return;
        }
        let submit = document.querySelector('.confirm-btn-invite');
        submit.disabled = true;
        let invite_form_data=$('#invite_form').serialize();
        toggleLoader(true);
        if (checkAbilityAddUser()) {
            await $.ajax({
                url: '{{url('/add-members-new')}}',
                method: "post",
                data: {
                    _token: $('meta[name="csrf-token"]').attr('content'),
                    members: invite_form_data,
                },
                dataType: 'json',
                beforeSend: function () {
                    toggleLoader(true);
                },
                success: (response) => {
                    toggleLoader(false);
                    if(response.status=='success'){
                        showSuccessModal('invite');
                        
                    }else{
                        toastr.warning(response.message); 
                    }
                },
                error: () => {
                    toggleLoader(false);
                    toastr.warning('{{__('error')}}');
                }
            })
        } else {
            toggleLoader(false);
            toastr.warning('{{__("You used all limit users. Upgrade your plan.")}}')
        }
    }
</script>
<script>
    const imagePicker = document.querySelector('#info-profile-img');
    const userImage = document.querySelector('#profile-image-to-show');
    imagePicker.addEventListener('change', imageChangeHandler);
    function imageChangeHandler(event) {
        $('#alert-div-info').addClass('d-none');
        var fuData = document.getElementById('info-profile-img');
        var FileUploadPath = fuData.value;
        var Extension = FileUploadPath.substring(FileUploadPath.lastIndexOf('.') + 1).toLowerCase();
        //The file uploaded is an image
        if (Extension == "gif" || Extension == "png" || Extension == "bmp" || Extension == "jpeg" || Extension == "jpg") {
            userImage.src = URL.createObjectURL(event.target.files[0]);
        } 
        else {
            $("#alert-div-info").text("{{__('Image is not valid')}}");
            $('#alert-div-info').removeClass('d-none');
        }                                               
    }
</script>
