    function submitPasswordLessForm(event){
        $('#alert-div-social').addClass('d-none');
        $("#alert-div-social").text("");
        $('#phone_social-error').addClass('d-none');
        $('#company_social-error').addClass('d-none');
        $('#company_social').removeClass('input-error-border');
        $('#phone_social').removeClass('input-error-border');
        event.preventDefault();
        let company_social = $('#company_social').val();
       
        const phoneNumberSocial = phoneInputSocial.getNumber();
        if(phoneNumberSocial!=''){
            if (!phoneInputSocial.isValidNumber()) {
                $("#phone_social").addClass('input-error-border');
                $("#phone_social-error").text("{{__('Invalid Phone Number')}}");
                $('#phone_social-error').removeClass('d-none');
                return;
            }
        }
        if(!company_social){
            $("#company_social").addClass('input-error-border');
            $("#company_social-error").text("{{__('This field is required')}}");
            $('#company_social-error').removeClass('d-none');
            return;
        }
        if(!phoneNumberSocial){
            $("#phone_social").addClass('input-error-border');
            $("#phone_social-error").text("{{__('This field is required')}}");
            $('#phone_social-error').removeClass('d-none');
            return;
        }
        // toggleLoader(true);
        $.ajax({
            type: 'POST',
            url: '{{ url('/signup-step-2-new-update') }}',
            data: {
                _token: '{{ csrf_token() }}',
                company: company_social,
                phone:phoneNumberSocial
            },
            beforeSend: function () {
                toggleLoader(true);
            },
            success: function(data) {
                if (data.status == true) {
                        window.location.replace('{{ url('/demo-dashboard')}}');
                } else {
                    window.location.replace('{{ url('/login')}}');
                }

            },
            error: function(error) {
                var html = '<ul class="m-0">';
                
                $.each(error.responseJSON.errors, function(key, value) {
                    html += '<li>' + value[0] + '</li>';
                    if(key=='company'){
                        $("#first_name").addClass('input-error-border');
                        $("#first_name-error").text(value[0]);
                        $('#first_name-error').removeClass('d-none');
                        return;
                    }
                    if(key=='last_name'){
                        $("#last_name").addClass('input-error-border');
                        $("#last_name-error").text(value[0]);
                        $('#last_name-error').removeClass('d-none');
                        return;
                    }
                });
                html += '</ul>';
                toggleLoader(false);
                // $('#alert-div-social').append(html);
                // $('#alert-div-social').removeClass('d-none');
            }
        });
    }
    function submitInfoForm(event) {
        $('#alert-div').addClass('d-none');
        $("#alert-div").text("");
        $('#first_name-error').addClass('d-none');
        $('#last_name-error').addClass('d-none');
        $('#company-error').addClass('d-none');
        $('#phone-error').addClass('d-none');
        $('#password-error').addClass('d-none');
        $("#first_name").removeClass('input-error-border');
        $("#last_name").removeClass('input-error-border');
        $("#company").removeClass('input-error-border');
        $("#phone").removeClass('input-error-border');
        $("#password").removeClass('input-error-border');
        $("#confirm_password").removeClass('input-error-border');
        event.preventDefault();
         // validate phone
        var getphoneNumber;
        var session_number="{{session('user_phone')}}";
        if(session_number==''){
            const phoneNumber = phoneInput.getNumber();
            if(phoneNumber!=''){
                if (!phoneInput.isValidNumber()) {
                    $("#phone").addClass('input-error-border');
                    $("#phone-error").text("{{__('Invalid Phone Number')}}");
                    $('#phone-error').removeClass('d-none');
                    return;
                }
                getphoneNumber = phoneNumber;
            }
        }else{
            getphoneNumber = session_number;
        }
        
        let first_name = $('#first_name').val();
        let last_name = $('#last_name').val();
        let company = $('#company').val();
        let password = $('#password').val();
        let phone = getphoneNumber;
        let confirm_password = $('#confirm_password').val();

        if(!first_name){
            $("#first_name").addClass('input-error-border');
            $("#first_name-error").text("{{__('This field is required')}}");
            $('#first_name-error').removeClass('d-none');
            return;
        }
        if(!last_name){
            $("#last_name").addClass('input-error-border');
            $("#last_name-error").text("{{__('This field is required')}}");
            $('#last_name-error').removeClass('d-none');
            return;
        }
        if(!company){
            $("#company").addClass('input-error-border');
            $("#company-error").text("{{__('This field is required')}}");
            $('#company-error').removeClass('d-none');
            return;
        }
        if(!phone){
            $("#phone").addClass('input-error-border');
            $("#phone-error").text("{{__('This field is required')}}");
            $('#phone-error').removeClass('d-none');
            return;
        }
        if(!password){
            $("#password").addClass('input-error-border');
            $("#password-error").text("{{__('This field is required')}}");
            $('#password-error').removeClass('d-none');
            return;
        }
        if(!confirm_password){
            $("#confirm_password").addClass('input-error-border');
            $("#password-error").text("{{__('This field is required')}}");
            $('#password-error').removeClass('d-none');
            return;
        }
        // toggleLoader(true);
        $.ajax({
            type: 'POST',
            url: '{{ url('/signup-step-2-new') }}',
            data: {
                _token: '{{ csrf_token() }}',
                first_name: first_name,
                last_name: last_name,
                company: company,
                phone:phone,
                password: password,
                confirm_password: confirm_password
            },
            beforeSend: function () {
                toggleLoader(true);
            },
            success: function(data) {
                if (data.status == true) {
                    if (data.is_phone_signup == true) {
                        // window.location.replace('{{ url('/demo-dashboard')}}');
                        personalInfoModal.hide();
                        otpModal.show();
                        toggleLoader(false);
                    } else if(data.is_email_signup == true) {
                        personalInfoModal.hide();
                        email_sent_modal.show();
                        toggleLoader(false);
                    }else{
                        window.location.replace('{{ url('/demo-dashboard')}}');
                    }
                } else {
                    window.location.replace('{{ url('/login')}}');
                }

            },
            error: function(error) {
                var html = '<ul class="m-0">';
                $.each(error.responseJSON.errors, function(key, value) {
                    if(key=='first_name'){
                        $("#first_name").addClass('input-error-border');
                        $("#first_name-error").text(value[0]);
                        $('#first_name-error').removeClass('d-none');
                        return;
                    }
                    if(key=='last_name'){
                        $("#last_name").addClass('input-error-border');
                        $("#last_name-error").text(value[0]);
                        $('#last_name-error').removeClass('d-none');
                        return;
                    }
                    if(key=='company'){
                        $("#company").addClass('input-error-border');
                        $("#company-error").text(value[0]);
                        $('#company-error').removeClass('d-none');
                        return;
                    }
                    if(key=='phone'){
                        $("#phone").addClass('input-error-border');
                        $("#phone-error").text(value[0]);
                        $('#phone-error').removeClass('d-none');
                        return;
                    }
                    if(key=='password'){
                        $("#password").addClass('input-error-border');
                        $("#password-error").text(value[0]);
                        $('#password-error').removeClass('d-none');
                    }
                    if(key=='confirm_password'){
                        $("#confirm_password").addClass('input-error-border');
                        $("#password-error").text(value[0]);
                        $('#password-error').removeClass('d-none');
                    }   
                    html += '<li>' + value[0] + '</li>';
                });
                html += '</ul>';
                toggleLoader(false);
                // $('#alert-div').append(html);
                // $('#alert-div').removeClass('d-none');
            }
        });
    }

    function submitOtpForm(event) {
        $('#otp-alert-div').addClass('d-none');
        $("#otp-alert-div").text("");
        event.preventDefault();
        const first=$('#o1').val();
        const second=$('#o2').val();
        const third=$('#o3').val();
        const four=$('#o4').val();
        const five=$('#o5').val();
        const six=$('#o6').val();

        const otp=first+second+third+four+five+six;
        $.ajax({
            type: 'POST',
            url: '{{ url('/signup-step-3-new') }}',
            data: {
                _token: '{{ csrf_token() }}',
                otp: otp
            },
            beforeSend: function () {
                toggleLoader(true);
            },
            success: function(data) {
                if(data.status==false){
                    toggleLoader(false);
                    toastr.warning("{{__('Wrong code, Please try Again')}}");
                }else{
                    window.location.replace("{{ url('/demo-dashboard') }}");
                }
                
            },
            error: function(error) {
                console.log(error);
                toggleLoader(false);
                toastr.warning("{{__('Too many attempts please try again later')}}");
            }
        })
    }
    function submitOtpFormModal(event) {
        $('#otp-alert-div').addClass('d-none');
        $("#otp-alert-div").text("");
        $('#otp-modal-error').addClass('d-none');
        event.preventDefault();
        const first_modal=$('#m1').val();
        const second_modal=$('#m2').val();
        const third_modal=$('#m3').val();
        const four_modal=$('#m4').val();
        const five_modal=$('#m5').val();
        const six_modal=$('#m6').val();

        const otp_modal=first_modal+second_modal+third_modal+four_modal+five_modal+six_modal;
        $.ajax({
            type: 'POST',
            url: '{{ url('/signup-step-3-new') }}',
            data: {
                _token: '{{ csrf_token() }}',
                otp: otp_modal
            },
            beforeSend: function () {
                toggleLoader(true);
            },
            success: function(data) {
                if(data.status==false){
                    $('#otp-modal-error').text("{{__('Wrong code, Please try Again')}}");
                    $('#otp-modal-error').removeClass('d-none');
                    toggleLoader(false);
                }else{
                    window.location.replace('{{ url('/demo-dashboard') }}');
                }
                
            },
            error: function(error) {
                console.log(error);
                toastr.warning("{{__('Too many attempts please try again later')}}");
                var html = '<ul class="m-0">';
                $.each(error.responseJSON.errors, function (key, value) {
                    html += '<li>'+value[0]+'</li>';
                });
                html += '</ul>';
                $('#otp-alert-div').append(html);
                // $('#otp-alert-div').removeClass('d-none');
                toggleLoader(false);
            }
        })
    }
</script>
