    window.fbAsyncInit = function () {
        FB.init({
            appId: {{env('FACEBOOK_APP_ID')}},
            cookie: true,
            xfbml: true,
            version: 'v12.0'
        });
    };
    function checkConnectionsCount() {
        let count_connection=0;
        $.ajax({
            url: '{{url('/count-connections')}}',
            method: "get",
            async: false,
            success: function (data) {
                count_connection = data.count;
            }
        })
        return count_connection;
        
    }
    function checkReportCount() {
        let count_dashboard=false;
        $.ajax({
            url: '{{url('/check-dashboards')}}',
            method: "get",
            async: false,
            success: function (data) {
                if(data.count==true){
                    count_dashboard =true;
                }else{
                    count_dashboard =false;
                }
            }
        })
        return count_dashboard;
    }
    function checkConnections() {
        let result = false;
        $.ajax({
            url: '{{url('/check-count-connections')}}',
            method: "get",
            async: false,
            success: function (data) {
                result = data.status;
            }
        })
        return result;
    };
    function login() {
        let createConnections = checkConnections();
        if (!createConnections) {
            return toastr.warning('{{__("You used all connections")}}. {{__("Upgrade your plan")}}');
        }
        switch (connection_type_to_add) {
            case 'facebook':
                fb_login();
                break;

            case 'twitter':
                twitter_login();
                break;

            case 'instagram':
                instagram_login();
                break;

            case 'facebook-ads':
                facebook_ads_login();
                break;

            case 'google-adv':
                googleAdsLogin();
                break;

            case 'google-analytics':
                googleAnalyticsLogin();
                break;

            case 'google-ads':
                googleAdsLogin();
                break;

            default:
                break;
        }
    };
    function fb_login() {
        let createConnections = checkConnections();
        if (!createConnections) {
            return toastr.warning('{{__("You used all connections")}}. {{__("Upgrade your plan")}}');
        }

        FB.login(function (response) {
            if (response.authResponse && response.status === 'connected') {
                access_token = response.authResponse.accessToken;
                user_id = response.authResponse.userID;
                $.ajax({
                    url: '{{url('facebook/callback')}}',
                    method: "post",
                    data: {
                        _token: $('meta[name="csrf-token"]').attr('content'),
                        id: user_id,
                        token: access_token,
                    },
                    dataType: 'json',
                    beforeSend: function () {
                        toggleLoader(true);
                    },
                    success: (response) => {
                        toggleLoader(false);
                        if (response.status) {
                            showSuccessModal('connection');
                            // window.location.href = '{{url('/signup/step-3')}}';
                        } else {
                            toastr.warning(response.message);
                        }
                    },
                    error: () => {
                        toggleLoader(false);
                    }
                })
            } else {
                console.log('User cancelled login or did not fully authorize.');
            }
        }, {
            scope: 'email, read_insights, pages_show_list, pages_read_engagement, public_profile',
        });
    }

